    // Get broadcast statistics
    const stats = await getBroadcastStats(nameOf)
    const previewText = fileId ? 'üì∑ Photo message' : `üìù Text message: ${message}`

    let statsText = ''
    if (stats) {
      statsText = `üìä Broadcast Statistics:\n‚Ä¢ Total users: ${stats.totalUsers}\n‚Ä¢ Batch size: ${stats.batchSize}\n‚Ä¢ Estimated time: ${stats.estimatedBatchTime} seconds\n‚Ä¢ Delay between batches: ${stats.delayBetweenBatches}s\n‚Ä¢ Max retries: ${stats.maxRetries}`
    } else {
      statsText = 'üìä Unable to get user statistics'
    }

    send(chatId, `${previewText}\n\n${statsText}\n\nReady to broadcast?`)
    return goto.adminConfirmMessage()
  }
  if (action === 'adminConfirmMessage') {
    if (message === t.back || message === t.no) return goto[admin.messageUsers]()
    if (message !== t.yes) return send(chatId, t.what)

    set(state, chatId, 'action', 'none')

    // Start broadcast with progress tracking
    send(chatId, 'üöÄ Starting broadcast... This may take a while for large user bases.')

    // Run broadcast in background to avoid blocking
    sendMessageToAllUsers(bot, info?.messageContent, info?.messageMethod, nameOf, chatId)
