const setupTelegramWebhook = async () => {
  if (TELEGRAM_BOT_ON !== 'true') {
    log('‚è≠Ô∏è  Telegram bot disabled, skipping webhook setup')
    return
  }

  try {
    const webhookUrl = `${SELF_URL}/telegram/webhook`

    // Delete any existing webhook first
    await bot.deleteWebHook()
    log('üóëÔ∏è  Deleted old webhook')

    // Set the new webhook
    await bot.setWebHook(webhookUrl)
    log('‚úÖ Telegram webhook set successfully')
    log(`üì° Webhook URL: ${webhookUrl}`)

    // Verify webhook was set
    const webhookInfo = await bot.getWebHookInfo()
    log('üìä Webhook info:', JSON.stringify(webhookInfo, null, 2))

    if (webhookInfo.url === webhookUrl) {
      log('‚úÖ Webhook verification passed')
    } else {
      log('‚ö†Ô∏è  Webhook URL mismatch!')
      log('Expected:', webhookUrl)
      log('Got:', webhookInfo.url)
    }
  } catch (error) {
    log('‚ùå Failed to set up Telegram webhook:', error.message)
    log('Error details:', error)
  }
}

